        onChange={setSelected}
        description="Just click on the options"
      >
        <Checkbox value="ham">\u{1F416} Ham</Checkbox>
        <Checkbox value="beef" disabled>
          \u{1F404} Beef (out of stock)
        </Checkbox>
        <Checkbox value="tuna">\u{1F41F} Tuna</Checkbox>
        <Checkbox value="tomatos">\u{1F345} Tomatos</Checkbox>
        <Checkbox value="onions">\u{1F9C5} Onions</Checkbox>
        <Checkbox value="pineapple">\u{1F34D} Pineapple</Checkbox>
      </CheckboxGroup>
      <hr />
      <pre>Selected values: {selected.join(', ')}</pre>
    </>
  );
};
