`,(0,l.jsx)(r,{file:"./combobox-async.demo.tsx",name:"combobox-async",source:`import { ComboBox, useAsyncList } from '@marigold/components';

export default () => {
  const list = useAsyncList<{ name: string }>({
    async load({ signal, filterText }) {
      const res = await fetch(
        \`https://swapi.py4e.com/api/people/?search=\${filterText}\`,
        { signal }
      );
      const json = await res.json();

      return {
        items: json.results,
      };
    },
  });
  return (
    <ComboBox
      label="Star Wars Character Lookup"
      value={list.filterText}
      onChange={list.setFilterText}
      items={list.items}
    >
      {(item: { name: string }) => (
        <ComboBox.Item key={item.name}>{item.name}</ComboBox.Item>
      )}
    </ComboBox>
  );
};
